import React from "react";
import "./App.css";
import logo from "./Pitchdeck - daimon.AI .png"; // Make sure this matches the file name

function App() {
  return (
    <div style={styles.container}>
      <img src={logo} alt="Daimon Logo" style={styles.logo} />
      <h1 style={styles.slogan}>
        your trusted guide through the LLM research landscape
      </h1>
      <input
        type="text"
        placeholder="Search..."
        style={styles.searchbar}
      />
    </div>
  );
}

const styles = {
  container: {
    backgroundColor: "#f5f6fa",
    height: "100vh",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
    textAlign: "center",
    padding: "0 20px",
    fontFamily: "'Poppins', sans-serif",
  },
  logo: {
    height: "200px",
    marginBottom: "40px",
    animation: "fadeIn 1s ease-out",
  },
  slogan: {
    fontSize: "2.6rem",
    fontWeight: 700,
    color: "#1e1e35",
    marginBottom: "30px",
    maxWidth: "900px",
    animation: "fadeIn 1.3s ease-out",
    lineHeight: 1.4,
  },
  searchbar: {
    padding: "14px 24px",
    fontSize: "1rem",
    borderRadius: "12px",
    border: "1px solid #ddd",
    outline: "none",
    width: "360px",
    maxWidth: "90%",
    backgroundColor: "#fff",
    boxShadow: "0 4px 18px rgba(0, 0, 0, 0.08)",
    animation: "fadeIn 1.6s ease-out",
  },
};

export default App;
