import React from "react";
import "./App.css";
import logo from "./Pitchdeck - daimon.AI .png"; // Make sure this matches the file name

function App() {
  return (
    <div style={styles.container}>
      <img src={logo} alt="Daimon Logo" style={styles.logo} />
      <p style={styles.slogan}>your trusted guide through the research landscape</p>
      <input
        type="text"
        placeholder="Search..."
        style={styles.searchbar}
      />
    </div>
  );
}

const styles = {
  container: {
    backgroundColor: "#f7f7fb",
    height: "100vh",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    fontFamily: "'Inter', sans-serif",
    padding: "0 20px",
    textAlign: "center",
  },
  logo: {
    height: "150px",
    marginBottom: "30px",
    opacity: 0.9,
    animation: "fadeIn 1.2s ease-out",
  },
  slogan: {
    fontSize: "2.2rem",
    fontWeight: 600,
    color: "#1c1c30",
    marginBottom: "40px",
    maxWidth: "800px",
    lineHeight: "1.4",
    animation: "fadeIn 1.6s ease-out",
  },
  searchbar: {
    padding: "14px 24px",
    fontSize: "1rem",
    borderRadius: "12px",
    border: "1px solid #ccc",
    outline: "none",
    width: "320px",
    maxWidth: "90%",
    background: "#fff",
    boxShadow: "0 4px 15px rgba(0, 0, 0, 0.1)",
    transition: "0.3s ease",
  },
};

// Optional: Add global keyframes in your App.css
// @keyframes fadeIn {
//   from { opacity: 0; transform: translateY(10px); }
//   to { opacity: 1; transform: translateY(0); }
// }

export default App;
