import React, { useEffect, useState } from "react";
import { fetchLLMPapers } from "../api/paperApi";

const PaperList = () => {
  const [papers, setPapers] = useState([]);
  const [loading, setLoading] = useState(true);

  const getPapers = async () => {
    setLoading(true);
    const result = await fetchLLMPapers();
    setPapers(result);
    setLoading(false);
  };

  useEffect(() => {
    getPapers();
  }, []);

  return (
    <div style={styles.wrapper}>
      <div style={styles.header}>
        <span style={styles.icon}>ðŸ“„</span>
        <h3 style={styles.title}>LLM Papers</h3>
        <button style={styles.refreshButton} onClick={getPapers}>âŸ³ Refresh</button>
      </div>
      {loading ? (
        <p style={styles.loading}>Loading papers...</p>
      ) : (
        <ul style={styles.paperList}>
          {papers.map((paper, idx) => (
            <li key={idx} style={styles.paperItem}>
              {paper.title || "Untitled"}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

const styles = {
  wrapper: {
    backgroundColor: "#fff",
    padding: "24px",
    borderRadius: "12px",
    boxShadow: "0 4px 20px rgba(0, 0, 0, 0.06)",
    width: "100%",
    maxWidth: "700px",
    margin: "40px auto 0",
    fontFamily: "'Poppins', sans-serif",
  },
  header: {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
    marginBottom: "16px",
  },
  icon: {
    fontSize: "1.5rem",
    marginRight: "10px",
  },
  title: {
    fontSize: "1.4rem",
    fontWeight: "600",
    color: "#1e1e35",
    margin: 0,
    flex: 1,
    textAlign: "left",
  },
  refreshButton: {
    backgroundColor: "#e0e0e0",
    border: "none",
    borderRadius: "8px",
    padding: "8px 16px",
    cursor: "pointer",
    fontSize: "0.9rem",
    fontWeight: 500,
  },
  paperList: {
    listStyle: "none",
    paddingLeft: "0",
    marginTop: "10px",
  },
  paperItem: {
    padding: "10px 0",
    borderBottom: "1px solid #eee",
    fontSize: "1rem",
    textAlign: "left",
  },
  loading: {
    fontStyle: "italic",
    color: "#555",
    textAlign: "left",
  },
};

export default PaperList;
